        mockPrice = _price;
        mockRoundId++;
        mockUpdatedAt = _timestamp;
    }

    function getRoundData(
        uint80 _roundId
    )
        external
        view
        override
        returns (
            uint80 roundId,
            int256 answer,
            uint256 startedAt,
            uint256 updatedAt,
            uint80 answeredInRound
        )
    {
        return (_roundId, mockPrice, mockUpdatedAt, mockUpdatedAt, _roundId);
    }

    function latestRoundData()
        external
        view
        override
        returns (
            uint80 roundId,
            int256 answer,
            uint256 startedAt,
            uint256 updatedAt,
            uint80 answeredInRound
        )
    {
        return (
            mockRoundId,
            mockPrice,
            mockUpdatedAt,
            mockUpdatedAt,
            mockRoundId
        );
    }
}

